mapper.select:
	select	distinct p.*, 
			ph.product_photo,
			ph.color
	from	product p,
			product_photo ph,
			product_detail pd
	where	p.product_no = ph.product_no
		and p.product_no = pd.product_no
		and ph.batch = 1

			and gender in

				#{g}



			and ph.color in

				 #{c}



		and product_size in

				#{s}



		and 


					price between 0 and #{p}


					price between 50000 and #{p}


					price between 100000 and #{p}


					price between 150000 and #{p}



order by	p.product_no desc

select 	* 
	from 	(select	a.*,
					rowNum rn
			from	(select	p.*,
							ph.product_photo,
							ph.color			
					from	product p,
							product_photo ph
					where	p.product_no = ph.product_no
						and ph.batch = 1
				order by	p.product_no desc) a
		) 
where	rn between #{startRow} and #{endRow}

select 	* 
	from 	(select	a.*,
					rowNum rn
			from	(select	distinct p.*, 
							ph.product_photo,
							ph.color
					from	product p,
							product_photo ph,
							product_detail pd
					where	p.product_no = ph.product_no
						and p.product_no = pd.product_no
						and ph.batch = 1

							and gender in

								#{g}



							and ph.color in

								 #{c}



						and product_size in

								#{s}



						and 


									price between 0 and #{p}


									price between 50000 and #{p}


									price between 100000 and #{p}


									price between 150000 and #{p}



				order by	p.product_no desc) a
			) 
where	rn between #{startRow} and #{endRow}

	select	count(*)
	from	product p,
			product_photo ph,
			product_detail pd
	where	p.product_no = ph.product_no
		and p.product_no = pd.product_no
		and ph.batch = 1

			and gender in

				#{g}



			and ph.color in

				 #{c}



		and product_size in

				#{s}



		and 


					price between 0 and #{p}


					price between 50000 and #{p}


					price between 100000 and #{p}


					price between 150000 and #{p}



order by	p.product_no desc
