mapper.select:
select	p.*,
		pp.product_photo
from	product p,
		(select	*
		from	product_photo
		where	product_no = #{product_no} 
			and batch = 1) pp
where	p.product_no = #{product_no}

select	wishNo
from	wish
where	productNo = #{productNo}
and		email = #{email}

mapper.delete:
delete	from	wish
where			productNo = #{productNo}
			and email = #{email}
