mapper.select:
select	p.*,
		pp.product_photo
from	product p,
		(select	*
		from	product_photo
		where	product_no = #{productNo} 
			and batch = 1) pp
where	p.product_no = #{productNo}

select	*
from	wish
where	product_no = #{productNo}
and		email = #{email}

mapper.delete:
delete	from	wish
where			product_no = #{productNo}
			and email = #{email}
